$(document).ready(function(){
  setup_datepickers();
});

function setup_datepickers() {
  $('.datepicker:text').livequery(function() {
    $(this).datepicker({
      showOn: 'button',
      buttonImage: '/images/datepicker-icon.png',
      buttonImageOnly: true,
      buttonText: 'Open calendar to pick a date',
      dateFormat: 'dd/mm/yy',
      showAnim: ''
    });
  });
}
